    // Adds a menu item to the tools menu
	
    editor.addMenuItem('hiddenlinks', {
        text: 'Закрытая ссылка',
        context: 'tools',
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Вставка неиндексируемой ссылки',
                body: [
                    {type: 'textbox', name: 'url', label: 'Адрес http...'},
					{type: 'textbox', name: 'text', label: 'Текст ссылки'}
                ],
                onsubmit: function(e) {
                    var html = '<span data-url="'+e.data.url+'">'+e.data.text+'</span>';
                    editor.insertContent(html);
                }
            });
        }
    });
	
